/* ==UserStyle==
@name Gmail Catppuccin
@namespace github.com/catppuccin/userstyles/styles/gmail
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/gmail
@version 0.1.3
@description Soothing pastel theme for Gmail
@author Catppuccin
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/gmail/catppuccin.user.css
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Agmail
@license MIT

@preprocessor less
@var select flavor "Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater*", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */
@-moz-document domain("mail.google.com") {
  :root {
    #catppuccin(@flavor, @accentColor);
  }

  /* prettier-ignore */
  @catppuccin: {
    @latte:     { @rosewater: #dc8a78; @flamingo: #dd7878; @pink: #ea76cb; @mauve: #8839ef; @red: #d20f39; @maroon: #e64553; @peach: #fe640b; @yellow: #df8e1d; @green: #40a02b; @teal: #179299; @sky: #04a5e5; @sapphire: #209fb5; @blue: #1e66f5; @lavender: #7287fd; @text: #4c4f69; @subtext1: #5c5f77; @subtext0: #6c6f85; @overlay2: #7c7f93; @overlay1: #8c8fa1; @overlay0: #9ca0b0; @surface2: #acb0be; @surface1: #bcc0cc; @surface0: #ccd0da; @base: #eff1f5; @mantle: #e6e9ef; @crust: #dce0e8; };
    @frappe:    { @rosewater: #f2d5cf; @flamingo: #eebebe; @pink: #f4b8e4; @mauve: #ca9ee6; @red: #e78284; @maroon: #ea999c; @peach: #ef9f76; @yellow: #e5c890; @green: #a6d189; @teal: #81c8be; @sky: #99d1db; @sapphire: #85c1dc; @blue: #8caaee; @lavender: #babbf1; @text: #c6d0f5; @subtext1: #b5bfe2; @subtext0: #a5adce; @overlay2: #949cbb; @overlay1: #838ba7; @overlay0: #737994; @surface2: #626880; @surface1: #51576d; @surface0: #414559; @base: #303446; @mantle: #292c3c; @crust: #232634; };
    @macchiato: { @rosewater: #f4dbd6; @flamingo: #f0c6c6; @pink: #f5bde6; @mauve: #c6a0f6; @red: #ed8796; @maroon: #ee99a0; @peach: #f5a97f; @yellow: #eed49f; @green: #a6da95; @teal: #8bd5ca; @sky: #91d7e3; @sapphire: #7dc4e4; @blue: #8aadf4; @lavender: #b7bdf8; @text: #cad3f5; @subtext1: #b8c0e0; @subtext0: #a5adcb; @overlay2: #939ab7; @overlay1: #8087a2; @overlay0: #6e738d; @surface2: #5b6078; @surface1: #494d64; @surface0: #363a4f; @base: #24273a; @mantle: #1e2030; @crust: #181926; };
    @mocha:     { @rosewater: #f5e0dc; @flamingo: #f2cdcd; @pink: #f5c2e7; @mauve: #cba6f7; @red: #f38ba8; @maroon: #eba0ac; @peach: #fab387; @yellow: #f9e2af; @green: #a6e3a1; @teal: #94e2d5; @sky: #89dceb; @sapphire: #74c7ec; @blue: #89b4fa; @lavender: #b4befe; @text: #cdd6f4; @subtext1: #bac2de; @subtext0: #a6adc8; @overlay2: #9399b2; @overlay1: #7f849c; @overlay0: #6c7086; @surface2: #585b70; @surface1: #45475a; @surface0: #313244; @base: #1e1e2e; @mantle: #181825; @crust: #11111b; };
  }

  #catppuccin(@lookup, @accent) {
    @rosewater: @catppuccin[@@lookup][@rosewater];
    @flamingo: @catppuccin[@@lookup][@flamingo];
    @pink: @catppuccin[@@lookup][@pink];
    @mauve: @catppuccin[@@lookup][@mauve];
    @red: @catppuccin[@@lookup][@red];
    @maroon: @catppuccin[@@lookup][@maroon];
    @peach: @catppuccin[@@lookup][@peach];
    @yellow: @catppuccin[@@lookup][@yellow];
    @green: @catppuccin[@@lookup][@green];
    @teal: @catppuccin[@@lookup][@teal];
    @sky: @catppuccin[@@lookup][@sky];
    @sapphire: @catppuccin[@@lookup][@sapphire];
    @blue: @catppuccin[@@lookup][@blue];
    @lavender: @catppuccin[@@lookup][@lavender];
    @text: @catppuccin[@@lookup][@text];
    @subtext1: @catppuccin[@@lookup][@subtext1];
    @subtext0: @catppuccin[@@lookup][@subtext0];
    @overlay2: @catppuccin[@@lookup][@overlay2];
    @overlay1: @catppuccin[@@lookup][@overlay1];
    @overlay0: @catppuccin[@@lookup][@overlay0];
    @surface2: @catppuccin[@@lookup][@surface2];
    @surface1: @catppuccin[@@lookup][@surface1];
    @surface0: @catppuccin[@@lookup][@surface0];
    @base: @catppuccin[@@lookup][@base];
    @mantle: @catppuccin[@@lookup][@mantle];
    @crust: @catppuccin[@@lookup][@crust];
    @accent-color: @catppuccin[@@lookup][@@accent];

    .ZG,
    .boo .aQl > .J-JN-M-I-Jm,
    .boo .aaa > .J-JN-M-I-Jm,
    .boo .ZE > .J-JN-M-I-Jm,
    .bs1 + .bs3,
    .btj + .aD,
    body {
      color: @text !important;
    }

    /* input box */
    .nr {
      background-color: @surface1 !important;
      color: @text !important;
    }

    .bs0 > .acM,
    .bti > .btg {
      color: @text !important;
    }

    /* quick settings */
    .IU {
      background: @base !important;
      box-shadow: inset 1px 0 0 @mantle !important;
    }
    .Q0,
    .VM .Q5,
    .VM .OG,
    .Q2,
    .bCh,
    .aIY,
    .a21,
    .ST,
    .OB {
      color: @text !important;
    }
    .Tj,
    .OD {
      color: @accent-color !important;
    }

    /* background */
    #loading {
      background: @base !important;
    }
    [style="background:#4285f4"] {
      background: @blue !important;
    }
    [style="background:#34a853"] {
      background: @green !important;
    }
    [style="background:#ea4335"] {
      background: @red !important;
    }
    .la-c.la-l {
      background: darken(@red, 10%);
    }
    .la-c.la-r {
      background: @yellow;
    }

    header,
    .nH.w-asV,
    .bkL,
    .bhZ.bym,
    .bhZ.bjB,
    .bhZ.bym.baA {
      background: @crust !important;
    }

    /* links */
    .aRq {
      color: @sapphire !important;
      &:hover {
        color: @teal !important;
      }
    }

    /* header */
    .aeH,
    .aqK {
      background: @mantle !important;
    }

    /* search */
    .gb_Lc .gb_Ee {
      background: @surface0 !important;
    }
    .gb_Lc .gb_3e,
    .gb_Ee.gb_Fe .gb_Ze {
      color: @text !important;
    }
    .gb_Ee.gb_Fe button svg {
      color: @text !important;
      opacity: 1;
    }
    .aRp {
      background: @surface0 !important;
    }
    .gssb_i {
      background: @surface1 !important;
      color: @text !important;
    }
    .gssb_e,
    .gssb_m {
      background: @surface0 !important;
      color: @text !important;
    }

    /* left bar */
    .CL.Q7:hover,
    .CL.Wj:hover,
    .CL.Q7.NQ,
    .CL.Wj.NQ,
    .TK .TO.nZ:active,
    .TK .TO.ol:active,
    .TK .TO.nZ,
    .TK .TO.ol,
    .byl .TK .nZ.aBP,
    .byl .TK .nZ.aS3,
    .byl .TK .nZ.aS4,
    .byl .TK .nZ.aS5,
    .byl .TK .nZ.aS6 {
      background: @surface0 !important;
    }
    .TO.NQ,
    .n6 .ah9:hover,
    .n6 .ah9.aiu:hover,
    .n6 .ah9:focus,
    .TK .TO:active,
    .n6 .ah9.aiu:active {
      background: @surface1 !important;
    }
    .aAv,
    .TO .nU > .n0,
    .TO.NQ .nU > .n0,
    .TO.nZ .nU > .n0,
    .ah9 > .CJ,
    .n3 > .CL > .CK {
      color: @text !important;
    }
    .h0,
    .Dj {
      color: @subtext0 !important;
    }

    /* right bar */
    .bAw .brC-aT5-aOt-Jw {
      background: @crust !important;
    }
    .brC-aMv-auO .bse-bvF-I.aT5-aOt-I-JW .aT5-aOt-I-JX-atM-J6,
    .brC-aMv-auO .bse-bvF-I.aT5-aOt-I-JO .aT5-aOt-I-JX-atM-J6 {
      background: lighten(@crust, 4%);
    }
    /* inbox area */
    .H2.HD {
      background: @surface0 !important;
      border-color: @surface1 !important;
    }
    .bkK > .nH {
      background: @mantle !important;
    }
    /* inbox item */
    .yO {
      background: @mantle !important;
      &:hover {
        box-shadow:
          inset 1px 0 0 @surface0,
          inset -1px 0 0 @surface0,
          0 0 4px 0 @base,
          0 0 6px 2px @base !important;
      }
    }
    /* quick text */
    .y2 {
      color: @subtext0 !important;
    }
    .aeJ,
    .aRs {
      .J-KU:hover {
        background: @surface0 !important;
      }
      background: @base !important;
      .aRu {
        .aRv {
          color: @subtext0 !important;
        }
        color: @subtext1 !important;
      }
    }
    .aAA.J-KU-Jg-K9 {
      background: @base !important;
    }
    .J-KU-KO.aAy {
      .aKz {
        color: @accent-color !important;
      }
      &::before {
        background: @accent-color !important;
      }
    }
    .aKx > .aKz {
      color: @subtext0 !important;
    }
    .y6,
    .bA4 {
      color: @text !important;
    }
    /* buttons hovers */
    .T-I-JW.amD::before,
    .T-I-JW.adg::before,
    .T-I-JW > .asa::before {
      background: @surface0 !important;
    }
    /* forward/reply buttons */
    .amn > .ams {
      color: @text !important;
    }
    .amr .amn > .ams {
      background: @base !important;
      border-color: @surface0 !important;
    }
    /* send one now */
    .x0 {
      color: @text !important;
    }

    /* theme */
    div.Kj-JD {
      background-color: @base;
    }
    .a80.Kj-JD-K7 {
      box-shadow: 0 5px 10px -5px @crust;
    }
    .a81 {
      border-color: @surface0;
    }

    /* no star warning */
    .Tm .TC {
      background: @base !important;
      color: @text !important;
    }

    /* dropdown */
    .ZF-z6,
    .ZF-zT,
    .ZF-Av .lJ,
    .ZF-Av .lN,
    .aaZ,
    .aoT,
    .J-M,
    .bAp.b8.UC .vh,
    .ajA,
    .nH .Hy .m,
    .J-N-JT,
    .J-JK-JT,
    .J-LC-JT,
    form[role="search"],
    form[role="search"] table,
    form[role="search"] div,
    form[role="search"] td {
      background: @surface0 !important;
    }

    div.Kj-JD-Jl > button.J-at1-atl,
    div.Kj-JD-Jl > button.J-at1-auR {
      background: @accent-color !important;
      color: @crust !important;
    }
    div.Kj-JD-Jl > button,
    .bBh .Kj-JD-Jl > .J-at1-auR,
    .Kj-JD-K7-K0,
    .J-N {
      color: @text !important;
    }
    .J-N-JT,
    .J-N-JW {
      color: @text !important;
      background: @surface2 !important;
    }
    .J-N-JT .J-N-Jz,
    .J-N-JW .J-N-Jz {
      color: @text !important;
    }
    .T-I-Kq > .asa::before,
    .T-I-Kq.T-I-JO > .asa::before {
      background: @surface1 !important;
    }

    /* compose button */
    .T-I-KE {
      background: @accent-color !important;
      color: @crust !important;
      & when (@lookup =latte) {
        color: @text !important;
      }
      &:hover {
        background: darken(@accent-color, 10%);
        color: @crust !important;
      }
    }

    /* drive usage */
    .aiC {
      background: @overlay0 !important;
    }
    .aiA {
      background: @accent-color !important;
    }
    /* icons */
    .aAy > .aKp,
    .xY > .T-Jo,
    .T-I .T-I-J3,
    td.apU > .T-KT.aXw::before {
      filter: brightness(0) saturate(100%) invert(28%) sepia(17%) saturate(835%)
        hue-rotate(196deg) brightness(150%) contrast(75%);
    }
    td.apU > .T-KT.T-KT-Jp::before {
      filter: brightness(0) saturate(100%) invert(28%) sepia(17%) saturate(835%)
        hue-rotate(337deg) brightness(300%);
    }
    .J-N-JX,
    .J-Ph-hFsbo,
    .OB,
    .Q1:not(:checked) + .Vo::before,
    .SV {
      filter: invert(1);
    }

    /* svgs */
    .gb_Mc svg,
    .gb_Rc.gb_Vc svg,
    .gb_Mc .gb_dd .gb_ld,
    .gb_Mc .gb_dd .gb_Lc,
    .gb_Mc .gb_dd .gb_fd,
    .gb_Rc.gb_Vc .gb_ld {
      color: @subtext0;
    }

    /* toolbar */
    .Hl,
    .Hq,
    .Ha,
    [role="menuitemcheckbox"] > div > div,
    [role="listbox"] .J-Z-M-I-J6-H > .J-Z-M-I-JG,
    div.ajR .ajT,
    .btC .dv,
    .btC .aaA.a1,
    .btC .J-N-JX.a1,
    .btC .aaA.e5,
    .aaZ .J-N-JX.e5,
    .btC .aaA.QT,
    .btC .J-N-JX.QT,
    .btC .aaA.aA7,
    .aaZ .J-N-JX.aA7,
    .btC .aaA.buc,
    .btC .J-N-JX.buc,
    .btC .aaA.BP,
    .aaZ .J-N-JX.BP,
    .btC .aaA.a5,
    .btC .aaA.a2X,
    .aaZ .J-N-JX.a5,
    .aaZ .J-N-JX.a2X,
    [role="toolbar"] [role="button"]:not(.H2, .Ol) {
      filter: invert(1) !important;
    }

    /* advanced settings */
    div.Kj-JD-Jz {
      color: @subtext0 !important;
    }
    .alO,
    .v .fY,
    .v .f1,
    .r4 {
      background: @base !important;
    }
    .v .f1,
    .v .f1 .f0 {
      color: @text !important;
    }
    .qL {
      color: black !important;
    }
    .v .fZ {
      box-shadow: inset 0 -2px 0 0 @accent-color;
      .f0 {
        color: @accent-color !important;
      }
    }
    .alP,
    .sA,
    .r4 .e {
      color: @sapphire !important;
      &:hover {
        color: @teal !important;
      }
    }
    .Ze {
      background: @surface0 !important;
    }
    .a8Y > .T-I-ax7 {
      background: @surface0 !important;
      color: @text !important;
      &:hover {
        background: @surface1 !important;
      }
    }
    .T-I-atl {
      background: @accent-color !important;
      color: @crust !important;
      &:hover {
        background: lighten(@accent-color, 10%) !important;
      }
    }

    /* loading */
    #explosion_clipper_div > .la-i > div {
      background: @surface0 !important;
    }

    #nlpt {
      background-color: @surface0 !important;
      &::before {
        background-color: @overlay0 !important;
      }
    }

    .la-b > .la-l,
    .la-b > .la-r,
    .la-b > .la-m {
      background: @surface1 !important;
    }

    .la-k .la-l,
    .la-k .la-r {
      border-color: @base !important;
    }

    .la-k .la-m {
      background: @base !important;
      clip-path: polygon(47% 100%, 100% 47%, 100% 100%);
    }

    .la-i > .la-l,
    .la-i > .la-r {
      border-color: @surface0 !important;
    }

    .la-i > .la-m {
      background: @surface0 !important;
    }

    .msgb {
      color: @text;
      a {
        color: @accent-color !important;
      }
    }
  }
}
// vim:ft=less

/* ==UserStyle==
@name NixOS Wiki Catppuccin
@namespace github.com/catppuccin/userstyles/styles/nixos.wiki
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/nixos.wiki
@version 0.0.4
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/nixos.wiki/catppuccin.user.css
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Anixos.wiki
@description Soothing pastel theme for NixOS Wiki
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire*", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */
@-moz-document domain('nixos.wiki') {
  #catppuccin(@lookup, @accent-color) {
    @rosewater: @catppuccin[@@lookup][@rosewater];
    @flamingo: @catppuccin[@@lookup][@flamingo];
    @pink: @catppuccin[@@lookup][@pink];
    @mauve: @catppuccin[@@lookup][@mauve];
    @red: @catppuccin[@@lookup][@red];
    @maroon: @catppuccin[@@lookup][@maroon];
    @peach: @catppuccin[@@lookup][@peach];
    @yellow: @catppuccin[@@lookup][@yellow];
    @green: @catppuccin[@@lookup][@green];
    @teal: @catppuccin[@@lookup][@teal];
    @sky: @catppuccin[@@lookup][@sky];
    @sapphire: @catppuccin[@@lookup][@sapphire];
    @blue: @catppuccin[@@lookup][@blue];
    @lavender: @catppuccin[@@lookup][@lavender];
    @text: @catppuccin[@@lookup][@text];
    @subtext1: @catppuccin[@@lookup][@subtext1];
    @subtext0: @catppuccin[@@lookup][@subtext0];
    @overlay2: @catppuccin[@@lookup][@overlay2];
    @overlay1: @catppuccin[@@lookup][@overlay1];
    @overlay0: @catppuccin[@@lookup][@overlay0];
    @surface2: @catppuccin[@@lookup][@surface2];
    @surface1: @catppuccin[@@lookup][@surface1];
    @surface0: @catppuccin[@@lookup][@surface0];
    @base: @catppuccin[@@lookup][@base];
    @mantle: @catppuccin[@@lookup][@mantle];
    @crust: @catppuccin[@@lookup][@crust];
    @accent: @catppuccin[@@lookup][@@accent-color];

    :root {
      color-scheme: if(@lookup = latte, light, dark);
    }

    html[data-darkreader-scheme="dark"] body::before {
      content: "Please turn off DarkReader for this domain when Catppuccin for NixOS Wiki is active.";
      background: @red;
      box-shadow:
        0 4px 16px fade(@crust, 10%),
        0 8px 32px fade(@crust, 5%);
      color: @crust;
      position: fixed;
      top: 0;
      right: 0;
      z-index: 99999;
      max-width: 24rem;
      padding: 0.75rem;
      margin: 0.5rem;
      border-radius: 5px;
    }

    body {
      background-color: @base;
    }
    hr {
      border-color: @surface2;
    }
    .navbar-default {
      color: @text;
      background-color: @mantle !important;
      background-image: none !important;
      border-color: transparent !important;

      .navbar-brand {
        color: @blue;
        &:hover {
          color: @sky;
        }
      }

      li {
        a {
          color: @text !important;
          &:hover {
            color: @subtext0 !important;
          }
        }
        &.nav.open > a,
        ul {
          background-color: @surface0 !important;
          background-image: none !important;
          a:hover {
            background-color: fade(@surface1, 50%) !important;
            background-image: none !important;
          }
        }
      }
      .active > a {
        color: @crust !important;
        &:hover {
          color: @base !important;
        }
        background: @blue !important;
      }
    }

    .search-query.form-control {
      color: @text;
      background-color: @surface0;
      border-color: @surface0;
    }

    * {
      color: @text;
    }

    a {
      color: @blue;
      &:hover {
        color: @sky;
      }
      &.new {
        color: @red;
        &:hover {
          color: @maroon;
        }
      }
    }

    .panel {
      background-color: @mantle;
      border-color: @mantle;
      .panel-heading {
        color: @text;
        background-color: @surface0 !important;
        border-bottom-color: @surface2;
        img {
          & when not (@lookup = latte) {
            filter: invert(100%);
          }
        }
      }
    }

    .btn-primary {
      color: @crust;
      background-color: @blue !important;
      background-image: none !important;
      border-color: @crust !important;
      &:hover {
        color: @crust !important;
        background-color: @sky !important;
        background-image: none !important;
      }
    }

    table {
      &,
      * {
        color: @text;
        border-color: @overlay0 !important;
        background-color: @mantle;
      }
      th {
        &,
        * {
          background-color: @surface0 !important;
        }
      }
    }

    blockquote,
    cite {
      border-left-color: @pink !important;
    }

    cite a {
      color: @pink;
      &:hover {
        color: @rosewater;
      }
    }

    pre,
    code {
      box-shadow: none !important;
      background-color: @mantle;
      border-color: @crust;
    }
    pre {
      color: @text;
    }
    code {
      color: @rosewater;
    }

    *[style*="background-color: #C1E5FF"] {
      color: @text !important;
      background-color: fade(@blue, 15%) !important;
      border-color: @blue !important;
    }

    *[style*="background-color: #FFD0B7"] {
      color: @text !important;
      background-color: fade(@peach, 15%) !important;
      border-color: @peach !important;
    }

    *[style*="background: #F6F6F6"] {
      color: @text !important;
      background-color: fade(@overlay0, 15%) !important;
      border-color: @overlay0 !important;
    }

    .mw-highlight,
    .mw-highlight > pre {
      color: @text !important;
      background-color: @mantle !important;

      // env assignment
      .nv {
        color: @text;
      }
      // dollar sign
      .gp {
        color: @pink;
      }
      // comment
      .c1,
      .cm {
        color: @overlay2;
      }
      // strings
      .s1,
      .s2,
      .se {
        color: @green;
      }
      // string interpolation
      .si {
        color: @pink;
      }
      // lhe
      .ss,
      .nb {
        color: @blue;
      }
      // operators
      .p,
      .o {
        color: @teal;
      }
      // numbers, boolean
      .mi,
      .no {
        color: @peach;
      }
      // keyword
      .k,
      .ow {
        color: @mauve;
      }
      // shebangs
      .ch {
        color: @pink;
      }
      // etc
      .go {
        color: @text;
      }
      // diff added
      .gi {
        color: @green;
      }
      // diff removed
      .gd {
        color: @red;
      }
      // diff header
      .gh {
        color: @text;
      }
      // diff changes
      .gu {
        color: @peach;
      }
    }

    .suggestions {
      .suggestions-result {
        background-color: @mantle;
        &:hover {
          background-color: mix(@base, @mantle, 50%);
        }
        color: @text;
        .highlight {
          color: @accent;
        }
      }
      .suggestions-results {
        border-color: @surface0 !important;
      }
      .suggestions-special {
        border-color: @surface0 !important;
        background-color: @mantle;
        &:hover {
          background-color: mix(@accent, @mantle, 15%);
        }
        .special-label {
          color: @subtext0;
        }
        .special-query {
          color: @text;
        }
      }
    }

    .toc {
      li.active > a {
        border-left-color: @accent !important;
      }
      ul.nav a:hover {
        background: @accent !important;
        &,
        * {
          color: @crust !important;
        }
      }
    }

    .page-header {
      border-bottom-color: @overlay2;
    }

    #catlinks {
      background-color: @mantle;
      border-color: @mantle;
    }

    #footer {
      background-color: @mantle;
    }
  }

  @media (prefers-color-scheme: light) {
    #catppuccin(@lightFlavor, @accentColor);
  }
  @media (prefers-color-scheme: dark) {
    #catppuccin(@darkFlavor, @accentColor);
  }

  // prettier-ignore
  @catppuccin: {
