    .sbsb_a,
    .sbdd_b {
      background: @mantle !important;
    }

    /* Transcript Button */
    .yt-spec-button-shape-next--call-to-action.yt-spec-button-shape-next--outline {
      color: @accent-color !important;
      border-color: @surface2 !important;

      &:hover {
        background-color: fadeout(@accent-color, 80) !important;
        border-color: fadeout(@accent-color, 80) !important;
      }
    }

    /* text color */
    .gsfs {
      color: @text !important;
    }

    /* Hover and keyboard select background */
    .sbsb_c.gsfs:hover,
    .sbsb_d {
      background-color: @surface0 !important;
    }

    .sbfl_b {
      background-color: @mantle !important;
      &:hover {
        background-color: @surface0 !important;
        color: @accent-color !important;
      }
    }

    .sbpqs_a {
      color: @accent-color !important;
    }

    .sbfl_a {
      color: @subtext0 !important;
    }

    .sbsb_i {
      color: @subtext0;
    }

    /* Keyboard select text color and hover text color */
    .sbsb_c:hover .sbqs_c,
    .sbsb_c.gsfs.sbsb_d .sbqs_c {
      color: @accent-color !important;
    }

    .ytp-swatch-background-color {
      background-color: @accent-color !important;
    }

    /* exmplation box */
    .explanation-box[correct] * {
      color: @crust !important;
    }

    /* history tab header */
    .page-header-view-model-wiz__page-header-title {
      color: @text;
    }

    /* Video Music Names */
    .yt-video-attribute-view-model__title {
      color: @text;
    }
    .yt-video-attribute-view-model__subtitle {
      color: @subtext0;
    }
    .yt-video-attribute-view-model__secondary-subtitle {
      color: @subtext0;
    }

    /* custom likes + anims */
    yt-animated-icon[animated-icon-type="LIKE"]
      > ytd-lottie-player
      > lottie-component
      > svg
      > g:nth-child(2)
      > g:nth-child(2) {
      /* not liked */
      > g:nth-child(2),
      > g:nth-child(4) {
        > path:nth-child(1) {
          stroke: @text !important;
        }
      }
      /* liked */
      > g:nth-child(1),
      > g:nth-child(3) {
        > path:nth-child(1) {
          fill: @accent-color !important;
        }
        > path:nth-child(2) {
          stroke: @accent-color !important;
        }
      }
    }

    /* iv branding */
    /* Popup for when you hover over the channel avatar in the video */
    .iv-branding .branding-context-container-inner {
      background-color: @base !important;
    }

    /* video desc */
    yt-attributed-string.ytd-text-inline-expander:nth-child(1)
      > span:nth-child(1)
      > span:nth-child(1) {
      color: @text !important;
    }
    #attributed-snippet-text > span:nth-child(1) > span:nth-child(1) {
      color: @text !important;
    }
    span[class="yt-core-attributed-string--link-inherit-color"][style="color: rgb(255, 255, 255);"] {
      color: @text !important;
    }

    .iv-branding .iv-branding-context-name {
      color: @text !important;
      text-shadow: none !important;
    }

    /* Cards */
    .iv-drawer-content {
      background-color: fadeout(@mantle, 5%) !important;
    }

    .iv-drawer-header-text {
      color: @text !important;
    }

    .iv-card-content,
    .ytp-ce-expanding-overlay-background,
    .ytp-cards-teaser .ytp-cards-teaser-text,
    .ytp-cards-teaser .ytp-cards-teaser-box {
      background-color: @base !important;
    }

    .iv-card-content > :first-child,
    .html5-video-player a,
    .iv-card h2,
    .ytp-cards-teaser .ytp-cards-teaser-text {
      color: @text !important;
    }

    /* Player tooltip background */
    .ytp-tooltip-text {
      background: @surface0 !important;
    }

    .iv-card:hover .iv-card-primary-link,
    .ytp-ce-website-title {
      color: @accent-color !important;
    }

    .ytp-popup {
      background: @surface0 !important;
    }

    tp-yt-paper-dialog {
      background-color: @base !important;
    }

    /* live badges */
    .badge-style-type-live-now-alternate,
    ytd-thumbnail-overlay-time-status-renderer[overlay-style="LIVE"] {
      color: @crust !important;
      yt-icon svg {
        g path,
        path {
          fill: @crust !important;
        }
      }
    }
    .ytp-live-badge {
      &::before {
        background: @subtext1 !important;
      }
      &[disabled]::before {
        background: @accent-color !important;
      }
    }

    /* notification idicator */
    .yt-spec-icon-badge-shape--type-notification
      .yt-spec-icon-badge-shape__badge {
      background-color: @accent-color !important;
      color: @crust !important;
    }
    .yt-spec-icon-badge-shape--style-overlay .yt-spec-icon-badge-shape__icon {
      color: var(--yt-spec-icon-active-other) !important;
    }
    .yt-spec-icon-badge-shape {
      color: var(--yt-spec-icon-active-other) !important;
    }

    /* bell next to subscription button */
    .yt-animated-icon svg {
      g path,
      path {
        fill: @text !important;
      }
    }

    /* creator badges */
    ytd-author-comment-badge-renderer {
      --yt-basic-background-color: @surface0 !important;
      --yt-basic-foreground-title-color: @surface0 !important;
      --ytd-author-comment-badge-background-color: @surface0 !important;
      --ytd-author-comment-badge-name-color: @text !important;
      --ytd-author-comment-badge-icon-color: @text !important;
      padding-right: 6px;
      padding-left: 8px;
    }

    /* ambient mode */
    #cinematics {
      mix-blend-mode: lighten;
    }

    /* badges e.g. popular */
    yt-chip-cloud-chip-renderer[chip-style="STYLE_DEFAULT"][selected],
    yt-chip-cloud-chip-renderer[chip-style="STYLE_HOME_FILTER"][selected] {
      background: @accent-color !important;
      color: @crust !important;
    }

    /* Search border */
    ytd-searchbox[has-focus] #container.ytd-searchbox {
      border-color: @accent-color !important;
    }

    /* New Feature inline banner */
    .ytd-brand-video-singleton-renderer {
      background: @surface0 !important;
      color: @text !important;
    }
    yt-button-shape a {
      border-color: @text !important;
      color: @text !important;
    }

    /* Chapter overview buttons */
    #share-button,
    #repeat-button {
      button {
        color: @text !important;
      }
    }

    /* Error page */
    #error-page {
      background-color: @base !important;
    }
    .search_plugin_added {
      background-color: @base;
    }

    #error-page-content {
      color: @text !important;
      text-shadow: none;
      background-color: @base !important;
      > p {
        color: @text !important;
        text-shadow: none;
      }
      #masthead-search-terms.masthead-search-terms-border {
        border-color: @accent-color !important;
        box-shadow: none !important;
      }
      #yt-masthead {
        color: @text !important;
        text-shadow: none;
        #logo-container {
          .logo {
            filter: invert(1) grayscale(1);
          }
          .content-region {
            color: @text !important;
            text-shadow: none;
          }
        }
        #masthead-search #masthead-search-terms-border {
          input[type="text"] {
            color: @text !important;
            &:focus {
              color: @accent-color !important;
            }
          }
        }
        .search-button {
          background-color: @mantle !important;
        }
      }
    }

    /* channel homepage */
    /* selected tab */
    .yt-tab-shape-wiz__tab--tab-selected {
      color: @accent-color !important;
    }
    .yt-tab-group-shape-wiz__slider {
      background-color: @accent-color !important;
    }

    /* unselected */
    .yt-tab-shape-wiz__tab {
      color: @subtext1;
    }

    /* hover */
    .yt-tab-shape-wiz:hover {
      .yt-tab-shape-wiz__tab {
        color: lighten(@accent-color, 7.5);
      }

      .yt-tab-shape-wiz__tab-bar {
        background-color: lighten(@accent-color, 7.5);
      }
    }

    /* yt end cards */
    .ytp-ce-channel-subscribers-text {
      color: @subtext0;
    }
    .ytp-ce-channel-metadata {
      color: @text;
    }
    .yt-ui-ellipsis {
      background-color: @base;
    }
    a.ytp-ce-link,
    a.ytp-ce-link:hover,
    a.ytp-ce-link:visited {
      color: @accent-color !important;
    }

    /* hopefully temporary (buttons) */
    .yt-spec-button-shape-next--mono.yt-spec-button-shape-next--tonal {
      background-color: @surface0;
      color: @text;
      &:hover {
        background-color: @surface1;
      }
    }
    .yt-spec-button-shape-next--call-to-action.yt-spec-button-shape-next--filled {
      color: @crust;
      background-color: @accent-color;
      &:hover {
        background-color: darken(@accent-color, 20);
      }
    }
    .yt-spec-touch-feedback-shape--touch-response
      .yt-spec-touch-feedback-shape__stroke {
      border-color: @surface1;
    }
    .yt-spec-touch-feedback-shape--touch-response-inverse
      .yt-spec-touch-feedback-shape__stroke {
      border-color: @accent-color;
    }

    ytd-guide-section-renderer.style-scope:nth-child(3) > div:nth-child(2),
    ytd-guide-section-renderer.style-scope:nth-child(4) > div:nth-child(2) {
      > ytd-guide-entry-renderer:nth-child(1)
        > a:nth-child(1)
        > tp-yt-paper-item:nth-child(1)
        > yt-icon:nth-child(1)
        > yt-icon-shape:nth-child(1)
        > icon-shape:nth-child(1)
        > div:nth-child(1)
        > svg:nth-child(1) {
        > g:nth-child(2) > g:nth-child(2) > path {
          &:nth-child(2) {
            fill: @base !important;
          }
        }
      }
      > ytd-guide-entry-renderer:nth-child(2)
        > a:nth-child(1)
        > tp-yt-paper-item:nth-child(1)
        > yt-icon:nth-child(1)
        > yt-icon-shape:nth-child(1)
        > icon-shape:nth-child(1)
        > div:nth-child(1)
        > svg:nth-child(1) {
        > path:nth-child(2) {
          fill: @base !important;
        }
        > path:nth-child(4) {
          fill: @base !important;
        }
      }
      > ytd-guide-entry-renderer:nth-child(3)
        > a:nth-child(1)
        > tp-yt-paper-item:nth-child(1)
        > yt-icon:nth-child(1)
        > yt-icon-shape:nth-child(1)
        > icon-shape:nth-child(1)
        > div:nth-child(1)
        > svg:nth-child(1) {
        > polygon:nth-child(2) {
          fill: @base !important;
        }
        > path:nth-child(3) {
          fill: @base !important;
        }
      }
      > ytd-guide-entry-renderer:nth-child(4)
        > a:nth-child(1)
        > tp-yt-paper-item:nth-child(1)
        > yt-icon:nth-child(1)
        > yt-icon-shape:nth-child(1)
        > icon-shape:nth-child(1)
        > div:nth-child(1)
        > svg:nth-child(1) {
        > path:nth-child(4) {
          fill: @base !important;
        }
      }
    }

    [fill="#F00"],
    [fill="red"],
    [fill="#FF0000"] {
      fill: @accent-color;
    }

    svg > g > polygon[fill="#fff"] {
      fill: @base !important;
    }

    .yt-spec-button-shape-next--call-to-action.yt-spec-button-shape-next--text {
      color: @accent-color;
      &:hover {
        background-color: fadeout(@accent-color, 90);
      }
    }

    .yt-spec-button-shape-next--mono.yt-spec-button-shape-next--outline,
    .yt-spec-button-shape-next--mono.yt-spec-button-shape-next--text {
      color: @text;
      &:hover {
        background-color: @surface0;
      }
    }

    .yt-spec-button-shape-next--mono.yt-spec-button-shape-next--outline {
      border-color: @surface0;
    }

    /* Scrollbar */
    ::-webkit-scrollbar {
      width: @scrollbar_width !important;
    }

    ::-webkit-scrollbar-track {
      background: @base !important;
    }

    ::-webkit-scrollbar-thumb {
      background: @accent-color !important;
    }

    /* text highlight */
    ::selection {
      background: fadeout(@accent-color, 80%) !important;
      color: @text !important;
    }
  }
}

@-moz-document url-prefix('https://www.youtube.com/redirect')
{
  /* youtube.com-redirect */
  :root[dark] {
    #catppuccin(@darkFlavor, @accentColor);
  }
  :root {
    #catppuccin(@lightFlavor, @accentColor);
  }

  #catppuccin(@lookup, @accent) {
    @rosewater: @catppuccin[@@lookup][@rosewater];
    @flamingo: @catppuccin[@@lookup][@flamingo];
    @pink: @catppuccin[@@lookup][@pink];
    @mauve: @catppuccin[@@lookup][@mauve];
    @red: @catppuccin[@@lookup][@red];
    @maroon: @catppuccin[@@lookup][@maroon];
    @peach: @catppuccin[@@lookup][@peach];
    @yellow: @catppuccin[@@lookup][@yellow];
    @green: @catppuccin[@@lookup][@green];
    @teal: @catppuccin[@@lookup][@teal];
    @sky: @catppuccin[@@lookup][@sky];
    @sapphire: @catppuccin[@@lookup][@sapphire];
    @blue: @catppuccin[@@lookup][@blue];
    @lavender: @catppuccin[@@lookup][@lavender];
    @text: @catppuccin[@@lookup][@text];
    @subtext1: @catppuccin[@@lookup][@subtext1];
    @subtext0: @catppuccin[@@lookup][@subtext0];
    @overlay2: @catppuccin[@@lookup][@overlay2];
    @overlay1: @catppuccin[@@lookup][@overlay1];
    @overlay0: @catppuccin[@@lookup][@overlay0];
    @surface2: @catppuccin[@@lookup][@surface2];
    @surface1: @catppuccin[@@lookup][@surface1];
    @surface0: @catppuccin[@@lookup][@surface0];
    @base: @catppuccin[@@lookup][@base];
    @mantle: @catppuccin[@@lookup][@mantle];
    @crust: @catppuccin[@@lookup][@crust];
    @accent-color: @catppuccin[@@lookup][@@accent];

    h1 {
      color: @text !important;
    }

    #redirect-main-text {
      color: @overlay2 !important;
      .bolded {
        color: @accent-color !important;
      }
    }

    /* search */
    #masthead-search-terms-border {
      border-color: @surface0 !important;
      background-color: @surface0 !important;
      box-shadow: fadeout(@crust, 10%) !important;
    }

    /* Community Cross-posting */
    ytd-shared-post-renderer[uses-compact-lockup]
      #repost-context.ytd-shared-post-renderer {
      background-color: var(--second-background);
    }
    #divider-line-container.ytd-shared-post-renderer {
      background-color: var(--yt-spec-10-percent-layer);
    }

    #masthead-search-terms {
      color: @text !important;
    }

    .search-button {
      background: @surface0 !important;
      border-color: @surface0 !important;
    }

    .search-button-content {
      filter: invert(1);
    }

    #redirect-backto-safety-button:hover {
      color: @text !important;
    }

    #logo-container .content-region {
      color: @text !important;
    }

    /* text highlight */
    ::selection {
      background: fadeout(@accent-color, 80%) !important;
      color: @text !important;
    }
  }
}

