/* ==UserStyle==
@name NixOS Search Catppuccin
@namespace github.com/catppuccin/userstyles/styles/nixos-search
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/nixos-search
@version 0.3.0
@description Soothing pastel theme for NixOS Search
@author Catppuccin
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/nixos-search/catppuccin.user.css
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Anixos-search
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue*", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */
@-moz-document domain("search.nixos.org") {
  @media (prefers-color-scheme: light) {
    #catppuccin(@lightFlavor, @accentColor);
  }

  @media (prefers-color-scheme: dark) {
    #catppuccin(@darkFlavor, @accentColor);
  }

  #catppuccin(@lookup, @accent) {
    @rosewater: @catppuccin[@@lookup][@rosewater];
    @flamingo: @catppuccin[@@lookup][@flamingo];
    @pink: @catppuccin[@@lookup][@pink];
    @mauve: @catppuccin[@@lookup][@mauve];
    @red: @catppuccin[@@lookup][@red];
    @maroon: @catppuccin[@@lookup][@maroon];
    @peach: @catppuccin[@@lookup][@peach];
    @yellow: @catppuccin[@@lookup][@yellow];
    @green: @catppuccin[@@lookup][@green];
    @teal: @catppuccin[@@lookup][@teal];
    @sky: @catppuccin[@@lookup][@sky];
    @sapphire: @catppuccin[@@lookup][@sapphire];
    @blue: @catppuccin[@@lookup][@blue];
    @lavender: @catppuccin[@@lookup][@lavender];
    @text: @catppuccin[@@lookup][@text];
    @subtext1: @catppuccin[@@lookup][@subtext1];
    @subtext0: @catppuccin[@@lookup][@subtext0];
    @overlay2: @catppuccin[@@lookup][@overlay2];
    @overlay1: @catppuccin[@@lookup][@overlay1];
    @overlay0: @catppuccin[@@lookup][@overlay0];
    @surface2: @catppuccin[@@lookup][@surface2];
    @surface1: @catppuccin[@@lookup][@surface1];
    @surface0: @catppuccin[@@lookup][@surface0];
    @base: @catppuccin[@@lookup][@base];
    @mantle: @catppuccin[@@lookup][@mantle];
    @crust: @catppuccin[@@lookup][@crust];
    @accent-color: @catppuccin[@@lookup][@@accent];

    /* Generic */
    html,
    body {
      background-color: @base;
      color: @text;
    }

    code,
    pre {
      color: @red;
      background-color: @surface0;
      border-color: @mantle;
    }

    a,
    a:hover,
    a:focus {
      color: @accent-color;
    }

    /* Navbar */
    .navbar .navbar-static-top {
      background-color: @mantle;
    }

    .navbar-inner {
      background-color: @mantle;
      background-image: none;
      border-color: @base;
    }

    .nav-tabs {
      border-bottom-color: @surface0;
    }

    .nav-tabs > .active > a,
    .nav-tabs > .active > a:hover,
    .nav-tabs > .active > a:focus {
      background-color: @base;
      border-color: @surface0;
      color: @text;
    }

    .navbar .nav > li > a {
      text-shadow: none;
      color: @subtext0;
    }

    .navbar .nav > li > a:focus,
    .navbar .nav > li > a:hover {
      color: @text;
    }

    .navbar .nav > .active > a,
    .navbar .nav > .active > a:hover,
    .navbar .nav > .active > a:focus {
      background-color: @surface0;
      color: @text;
    }

    /* Installation instructions tabs */
    .nav > li > a:hover,
    .nav > li > a:focus {
      background-color: @surface0;
      border-color: @surface0;
    }

    /* "Experimental" label */
    .label,
    .badge {
      background-color: @accent-color;
      color: @crust;
      text-shadow: none;
    }

    /* Buttons */
    .btn {
      background-color: @surface0;
      background-image: none;
      text-shadow: none;
      box-shadow: none;
      color: @text;
      border-color: @base;
    }

    .btn:focus {
      color: @text;
      background-color: @surface1;
    }

    .btn:hover,
    .btn:active,
    .btn.active,
    .btn.disabled,
    .btn[disabled] {
      background-color: @surface1;
      color: @text;
    }

    /* "Sort" menu */
    .dropdown-menu {
      background-color: @mantle;
      border-color: @crust;
      box-shadow: 0 5px 10px @mantle;
    }

    .dropdown-menu > li > a {
      color: @text;
    }

    .dropdown-menu > li > a:hover,
    .dropdown-menu > li > a:focus,
    .dropdown-submenu:hover > a,
    .dropdown-submenu:focus > a {
      background-color: @surface0;
      background-image: none;
    }

    .dropdown-menu .divider {
      background-color: @surface0;
      border-bottom-color: @surface0;
    }

    .dropdown .caret {
      border-top-color: @text;
    }

    /* Overrides menu item hover color */
    .search-page
      > .search-results
      > div
      > :first-child
      > div:first-child
      > ul
      > li
      > a {
      color: @text;
    }

    /* Loading indicator */
    .loader {
      color: @crust;
    }

    /* Input box */
    textarea,
    input[type="text"],
    input[type="password"],
    input[type="datetime"],
    input[type="datetime-local"],
    input[type="date"],
    input[type="month"],
    input[type="time"],
    input[type="week"],
    input[type="number"],
    input[type="email"],
    input[type="url"],
    input[type="search"],
    input[type="tel"],
    input[type="color"],
    .uneditable-input {
      background-color: @surface0;
      color: @text;
      border-color: @base;
    }

    textarea:focus,
    input[type="text"]:focus,
    input[type="password"]:focus,
    input[type="datetime"]:focus,
    input[type="datetime-local"]:focus,
    input[type="date"]:focus,
    input[type="month"]:focus,
    input[type="time"]:focus,
    input[type="week"]:focus,
    input[type="number"]:focus,
    input[type="email"]:focus,
    input[type="url"]:focus,
    input[type="search"]:focus,
    input[type="tel"]:focus,
    input[type="color"]:focus,
    .uneditable-input:focus {
      border-color: @accent-color;
      box-shadow:
        inset 0 1px 1px @crust,
        0 0 8px @accent-color;
    }

    /* Fix input box placeholder text */
    textarea::placeholder,
    input[type="text"]::placeholder,
    input[type="password"]::placeholder,
    input[type="datetime"]::placeholder,
    input[type="datetime-local"]::placeholder,
    input[type="date"]::placeholder,
    input[type="month"]::placeholder,
    input[type="time"]::placeholder,
    input[type="week"]::placeholder,
    input[type="number"]::placeholder,
    input[type="email"]::placeholder,
    input[type="url"]::placeholder,
    input[type="search"]::placeholder,
    input[type="tel"]::placeholder,
    input[type="color"]::placeholder,
    .uneditable-input::placeholder {
      color: @subtext0;
    }

    /* Search results */
    .search-page > .search-results > div > :nth-child(2) > li {
      border-bottom-color: @surface0;
    }

    .search-page > .search-results > div > :nth-child(2) > li > :first-child {
      color: @accent-color;
    }

    .search-page
      > .search-results
      > div
      > :nth-child(2)
      > li.package
      > :nth-child(5)
      > :nth-child(2)
      ul.nav-tabs
      > li
      > a {
      color: @text;
    }

    .search-page
      > .search-results
      > div
      > :nth-child(2)
      > li.package
      > :nth-child(5)
      > :nth-child(2)
      pre {
      color: @text;
      background-color: @mantle;
    }

    .search-page
      > .search-results
      > div
      > :nth-child(2)
      > li.package
      .result-item-show-more {
      background-color: @base;
      color: @text;
    }

    .search-page
      > .search-results
      > div
      > :nth-child(2)
      > li.package
      > :nth-child(3)
      > li {
      color: @text;
    }

    .search-page
      > .search-results
      > div
      > :nth-child(2)
      > li.package
      > :nth-child(5)
      > :nth-child(2)
      div.tab-content {
      border-color: @surface0;
    }

    .search-page
      > .search-results
      > div
      > :nth-child(2)
      > li.option
      > :nth-child(2)
      > div:nth-child(2n)
      pre
      code {
      color: @text;
      background-color: @mantle;
    }

    /* Search sidebar */
    .search-page ul.search-sidebar > li {
      border-color: @surface0;
    }

    .search-page ul.search-sidebar > li > ul > li > a {
      color: @text;
    }

    .search-page ul.search-sidebar > li > ul > li > a:hover {
      background-color: @surface0;
    }

    .search-page ul.search-sidebar > li > ul > li > a.selected {
      color: @crust;
      background-color: @accent-color;
    }

    /* Override sidebar labels (number of packages) */
    .search-sidebar .label,
    .search-sidebar .badge {
      background-color: @surface0;
      color: @text;
      text-shadow: none;
    }

    /* Pager */
    .pager li > a,
    .pager li > span {
      color: @text;
      background-color: @surface0;
      border-radius: 5px;
    }

    .pager li > a:hover,
    .pager li > a:focus {
      background-color: @surface1;
    }

    .pager .disabled > a,
    .pager .disabled > a:hover,
    .pager .disabled > a:focus,
    .pager .disabled > span {
      color: @text;
      background-color: @mantle;
      border-radius: 5px;
    }
  }
}

