  .theme-mastodon-light,
  .skin-modern-light,
  .skin-mastodon-light {
    #mastodon(@lightFlavor, @accentColor);
  }

  .theme-contrast,
  .skin-modern-dark,
  .skin-contrast {
    #mastodon(@darkFlavor, @accentColor);
  }

  @media (prefers-color-scheme: light) {
    .theme-default,
    .skin-default {
      #mastodon(@lightFlavor, @accentColor);
    }
  }

  @media (prefers-color-scheme: dark) {
