    } // Inside primary buttons
    .xzloghq,
    ._acas:not(._acao) {
      &,
      svg {
        color: @base;
        fill: @base;
      }
    } // Secondary buttons
    .x1gjpkn9,
    ._acat,
    ._acap {
      &,
      a& {
        &,
        &:visited {
          color: @text;
        }
      }
    } // Profile banner
    div:has(> .x1mu97ne) {
      background-color: @crust;
      border-color: @overlay0;
      box-shadow: 0 10px 35px 2px fadeout(@dark_color, 0.7);
    } // Dialog overlay
    .x7r02ix[role="dialog"] {
      box-shadow: 0 5px 10px 0 fadeout(@dark_color, 0.5);
    } // Input placeholders
    ._ac4d::placeholder,
    ._aauy::placeholder,
