    }

    /* safe search toggle */
    button#safe-search-toggle[aria-checked="true"] {
      background: @accent-color !important;
    }

    /* NAVIGATION SIDEBAR */

    /* close icon */
    i.icon-close {
      color: @subtext0 !important;
    }

    /* item color */
    a[role="menuitem"] {
      color: @text !important;
    }

    /* navigation sidebar header color */
    div[role="menu"] > div[role="heading"] {
