      }
    }

    .pure-form-message-inline {
      color: @subtext0;
    }

    select,
    input[type="text"],
    input[type="number"],
    input[type="input"] {
      color: @text !important;
      background-color: @surface0 !important;
      border-color: @crust !important;
      box-shadow: none !important;
      &:active {
