@preprocessor less
@var select flavor "Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var checkbox zen "Zen Mode" 0
==/UserStyle== */
@-moz-document domain("ichi.moe") {
