    .has-tip:hover,
    .has-tip:focus {
      border-bottom-color: @sapphire;
      color: @sapphire;
    }

    label {
      color: @subtext1;
    }

    table {
      background: @base;
      & when (@lookup ="latte") {
        background-color: @surface0;
      }
      background-color: @surface1;
    }

    table tr th,
    table tr td {
      color: @subtext1;
    }

    .kanji-table td {
      & when (@lookup ="latte") {
        border-color: @surface0;
      }
      border-color: @surface1;
    }

    table tr.even,
    table tr.alt,
    table tr:nth-of-type(2n) {
      & when (@lookup ="latte") {
        background: fadeout(@surface0, 0.5);
      }
      background: fadeout(@surface1, 0.5);
    }

    .reading-table tr ~ tr > td {
      & when (@lookup ="latte") {
        border-color: @surface0;
      }
      border-color: @surface1;
    }

    table.kanji-match td {
      & when (@lookup ="latte") {
        border-color: @surface0;
      }
      border-color: @surface1;
    }

    tr.match-row-kanji,
    tr.match-row-reading {
      & when (@lookup ="latte") {
        border-color: @surface0;
      }
      border-color: @surface1;
    }

    img {
      filter: contrast(1.2) opacity(85%);
    }

    /* Options */
    & when (@zen = 1) {
      .header,
      footer,
      .landing-page {
        display: none;
      }

      div.wrapper {
        display: flex;
        flex-direction: column;
        align-items: center;
        place-content: center center;
        padding: 50px 0;
        min-height: 100vh;
        margin: 0;
      }

      .gloss-all {
        width: 100%;
        padding: 0 10px;
      }

      #div-ichiran-result {
        padding: 0 10px;
      }

      #div-kanji-form,
      #w-form {
        width: 100%;
      }

      #k-form > div > div,
      #w-form > div > div {
        width: 100%;
        max-width: 304px;
      }
    }
  }
}
