    .hooks__list tr.join:hover td {
      background-color: fade(@accent-color, 50%);
    }

    /* Topbar */
    #topnav.hover section:hover > a,
    #topnav section:active > a,
    #topnav div,
    .site-buttons .shown .toggle,
    .site-buttons .dropdown {
      background: @crust;
    }
    #topnav section > a {
      color: @subtext0;
    }
    #topnav.hover section:hover > a,
    #topnav section:active > a,
