    }
    #dasher_app .sub::before,
    .is-green::before {
      color: @green;
    }
    #challenge-app .empty {
      background: @crust;
    }

    /* Popup skining */
    dialog {
      background: @crust;
    }
    .game-setup .optional-config,
    .game-setup .ratings {
      background: @base;
    }
    .game-setup .radio input:checked + label {
      background: @green;
    }

    /* Text */
    .site-buttons .link,
    .button,
    .input,
    .optgroup,
    .select,
    .textarea,
    .lobby__box td.name a,
    .lobby__box__top .title,
    .lobby__box .user-link,
    .mini-game,
    .mini-game:hover,
    .text,
    .ublog-post-card__title,
    .lobby__counters a,
    .lobby__streams .stream,
    .lobby__timeline .entry a,
    .lobby__about a,
    .lobby__timeline,
    .lobby__counters,
    .tabs-horiz,
    .lobby__streams .more,
    .lobby__timeline .more,
    .dialog-content,
    button,
    input,
    optgroup,
    select,
    textarea {
      color: @subtext0;
    }
    .site-buttons .link:hover,
    .button.button-metal:hover,
    .radio input:checked + label {
