    }

    .bg-white\/20 {
      background-color: fade(@white, 20%);
    }

    .bg-white\/25 {
      background-color: fade(@white, 25%);
    }

    .bg-yellow-100 {
      background-color: lighten(@yellow, 20%);
    }

    .bg-yellow-200 {
      background-color: lighten(@yellow, 10%);
    }

    .bg-yellow-400 {
      background-color: @yellow;
    }

    .bg-yellow-500 {
      background-color: darken(@yellow, 10%);
    }

    .radix-state-checked\:bg-green-600[data-state="checked"] {
      background-color: darken(@green, 10%);
    }

    .hover\:bg-blue-700:hover {
      background-color: darken(@blue, 25%);
    }

    [class*="bg-transparent"] {
      background-color: transparent;
    }

    /* Gradients */

    .from-white {
      --tw-gradient-from: @white var(--tw-gradient-from-position);
    }

    /* Fill/stroke */

    .fill-yellow-500 {
      fill: @yellow;
    }

    .stroke-black {
      stroke: @black;
    }

    .stroke-brand-purple\/25 {
      stroke: fade(@accent-color, 25%);
    }

    .stroke-gray-400 {
      stroke: @subtext0;
    }

    /* Text */

    .\!text-black {
      color: @black !important;
    }

    .text-\[\#FE7600\] {
      color: mix(@yellow, @red);
    }

    .text-\[\#fff\] {
      color: @base;
    }

    .text-black {
      color: @black;
    }

    .text-black\/60 {
      color: fade(@black, 60%);
    }

    .text-black\/70 {
      color: fade(@black, 70%);
    }

    .text-blue-500 {
      color: lighten(@blue, 10%);
    }

    .text-blue-600 {
      color: @blue;
    }

    .text-brand-blue-800 {
      color: @blue;
    }

    .text-brand-purple {
      color: @accent-color;
    }

    .text-gray-100 {
      color: var(--gray-100);
    }

    .text-gray-300 {
      color: var(--gray-300);
    }

    .text-gray-400 {
      color: var(--gray-400);
    }

    .text-gray-500 {
      color: var(--gray-500);
    }

    .text-gray-600 {
      color: var(--gray-600);
    }

    .text-gray-700 {
      color: var(--gray-700);
    }

    .text-gray-800 {
      color: var(--gray-800);
    }

    .text-gray-900 {
      color: var(--gray-900);
    }

    .text-green-500 {
      color: lighten(@green, 10%);
    }

    .text-green-600 {
      color: @green;
    }

    .text-green-700 {
      color: darken(@green, 10%);
    }

    .text-green-800 {
      color: darken(@green, 20%);
    }

    .text-green-900 {
      color: darken(@green, 30%);
    }

    .text-orange-300 {
      color: lighten(mix(@yellow, @red), 15%);
    }

    .text-orange-400 {
      color: lighten(mix(@yellow, @red), 10%);
    }

    .text-orange-500 {
      color: mix(@yellow, @red);
    }

    .text-red-500 {
      color: @red;
    }

    .text-red-600 {
      color: lighten(@red, 10%);
    }

    .text-red-700 {
      color: @red;
    }

    .text-red-800 {
      color: darken(@red, 10%);
    }

    .text-white {
      color: @white;
    }

    .text-white\/25 {
      color: fade(@white, 25%);
    }

    .text-white\/50 {
      color: fade(@white, 50%);
    }

    .text-white\/80 {
      color: fade(@white, 80%);
    }

    .text-yellow-500 {
      color: @yellow;
    }

    .text-yellow-700 {
      color: darken(@yellow, 10%);
    }

    .text-yellow-800 {
      color: darken(@yellow, 20%);
    }

    .text-yellow-900 {
      color: darken(@yellow, 30%);
    }

    /* Dark mode overrides */
    /* stylelint-disable-next-line no-duplicate-selectors */
    & when not (@lookup = latte) {
      .dark\:bg-black {
        background-color: @black;
      }

      .dark\:bg-white {
        background-color: @white;
      }

      /* "Was this response better or worse" dialog button hover */
      .dark\:hover\:bg-gray-800:hover {
        background-color: transparent;
      }

      .dark\:text-white {
        color: @white;
      }

      .dark\:text-black {
        color: @black;
      }

      .dark\:text-\[\#D292FF\] {
        color: @mauve;
      }

      .dark\:focus\:border-white:focus {
        border-color: @text;
      }
    }

    /* Other */

    .ring-black {
      --tw-ring-color: @black;
    }

    .ring-black\/10 {
      --tw-ring-color: fade(@black, 10%);
    }

    .ring-white {
      --tw-ring-color: @base;
    }

    .ring-offset-black {
      --tw-ring-offset-color: @base;
    }

    [style="background-color: rgb(0, 0, 46);"],
    [style="background-color: rgb(255, 255, 219);"] {
      background-color: @crust !important;
    }
  }
}

